-- title:  movement demo
-- author: notnull games
-- desc:   humble beginnings
-- script: lua

local t=0
local x=109
local y=41
local direction = 0
local moving = false
local step_time = 10

-- print text in the center of the screen
function center_text(str)
	local width = print(str, 0, -6)
	print(str, (240 - width) // 2, (136 - 6) // 2)
end

music(0)

function TIC()
	t=t+1

	if btn(0) then
		y=y-1
		direction = 3
	end
	
	if btn(1) then
		y=y+1
		direction = 0
	end
	
	if btn(2) then
		x=x-1
		direction = 1
	end
	
	if btn(3) then
		x=x+1
		direction = 2
	end

	moving = btn(0) or btn(1) or btn(2) or btn(3)

	cls()
	
	center_text("Press arrows to move around")

	local frame = 1
	if moving then
		frame = t % (3 * step_time) // step_time
		if frame == 0 then
			sfx(56, "C-4")
		end
	end
	frame = frame * 2

	-- move a 2x2 princess sprite (selected by direction and time for animation) to x/y
	spr(256 + (direction * 32) + frame, x, y, 0, 1, 0, 0, 2, 2)

	-- show current location
	-- print(string.format("%dx%d", x, y), 0, 0)
	print(peek(0x13FFC))

end

